function chinaTermsDialog:init(layerNum,showCheckBox)
	self.layerNum=layerNum
	self:show()
	self.dialogLayer=CCLayer:create()

	local function nilFunc()
	end
	local touchDialogBg = LuaCCScale9Sprite:createWithSpriteFrameName("BlackAlphaBg.png",CCRect(10, 10, 1, 1),nilFunc)
	touchDialogBg:setTouchPriority(-(layerNum-1)*20-1)
	local rect=CCSizeMake(640,G_VisibleSizeHeight)
	touchDialogBg:setContentSize(rect)
	touchDialogBg:setOpacity(180)
	touchDialogBg:setPosition(ccp(0,0))
	self.dialogLayer:addChild(touchDialogBg)

	self:initTerm()

	sceneGame:addChild(self.dialogLayer,self.layerNum)
	self.dialogLayer:setPosition(getCenterPoint(sceneGame))
	return self.dialogLayer
end

function chinaTermsDialog:initTerm(type)
	if(self.bgLayer)then
		self.bgLayer:removeFromParentAndCleanup(true)
		self.bgLayer=nil
	end
	self.lbList={}
	local length=#self["strList"]
	for i=1,length do
		local align,size
		if(i==1 or i==2)then
			align=kCCTextAlignmentCenter
		else
			align=kCCTextAlignmentLeft
		end
		if(i==1)then
			size=24
		else
			size=22
		end
		local lb=GetTTFLabelWrap(self["strList"][i],size,CCSizeMake(self.dialogWidth - 50,0),align,kCCVerticalTextAlignmentCenter)
		lb:setAnchorPoint(ccp(0,0))
		lb:setPosition(ccp(5,0))
		table.insert(self.lbList,lb)
	end
	CCTexture2D:setDefaultAlphaPixelFormat(kCCTexture2DPixelFormat_RGBA8888)
    CCTexture2D:setPvrDefaultAlphaPixelFormat(kCCTexture2DPixelFormat_RGBA8888) 
    CCSpriteFrameCache:sharedSpriteFrameCache():addSpriteFramesWithFile("public/youhuaUI6.plist")
    CCSpriteFrameCache:sharedSpriteFrameCache():addSpriteFramesWithFile("public/youhuaUI2.plist")
    CCTexture2D:setDefaultAlphaPixelFormat(kCCTexture2DPixelFormat_RGBA4444)
    CCTexture2D:setPvrDefaultAlphaPixelFormat(kCCTexture2DPixelFormat_RGBA4444)

	self.dialogHeight = 700
	local function close()
		self:close()
	end
	local dialogBg=G_getNewDialogBg(CCSizeMake(self.dialogWidth,self.dialogHeight),"雷尚通行证用户协议",30,nil,self.layerNum,true,close)
	-- local dialogBg = LuaCCScale9Sprite:createWithSpriteFrameName("PanelHeaderPopup.png",CCRect(168, 86, 10, 10),nilFunc)
	self.bgLayer=dialogBg
    self.bgSize=CCSizeMake(self.dialogWidth,self.dialogHeight)
	self.bgLayer:setContentSize(self.bgSize)
	self.dialogLayer:addChild(self.bgLayer,1)
	self.dialogLayer:setTouchPriority(-(self.layerNum-1)*20-1)
	self.dialogLayer:setBSwallowsTouches(true)

	-- local titleLb=GetTTFLabel("雷尚通行证用户协议",30)
	-- titleLb:setAnchorPoint(ccp(0.5,0.5))
	-- titleLb:setPosition(ccp(self.dialogWidth/2,self.dialogHeight-titleLb:getContentSize().height-5))
	-- dialogBg:addChild(titleLb,1)
	local tvHeight,tvPosY = self.dialogHeight - 160,90
	local acceptPk = CCUserDefault:sharedUserDefault():getBoolForKey("acceptProtocolKey")
	if acceptPk==true then
		tvHeight = tvHeight + 70
		tvPosY = tvPosY - 70
	end
	local function callBack(...)
		return self:eventHandler(...)
	end
	local hd= LuaEventHandler:createHandler(callBack)
	local tv=LuaCCTableView:createWithEventHandler(hd,CCSizeMake(self.dialogWidth - 40,tvHeight),nil)
	tv:setTableViewTouchPriority(-(self.layerNum-1)*20-3)
	tv:setPosition(ccp(20,tvPosY))
	tv:setMaxDisToBottomOrTop(80)
	self.bgLayer:addChild(tv)

	if acceptPk == false then
		local function onBack()
			self:close()
			CCUserDefault:sharedUserDefault():setBoolForKey("acceptProtocolKey",true)
			CCUserDefault:sharedUserDefault():flush()
			eventDispatcher:dispatchEvent("user.accept.protocol",{})
		end
		local okItem=GetButtonItem("newGreenBtn.png","newGreenBtn_down.png","newGreenBtn.png",onBack,2,getlocal("accpet"),25/0.7)
		okItem:setScale(0.7)
		local okBtn=CCMenu:createWithItem(okItem)
		okBtn:setPosition(ccp(self.dialogWidth/2,50))
		okBtn:setTouchPriority(-(self.layerNum-1)*20-2)
		dialogBg:addChild(okBtn)
	end
end

function chinaTermsDialog:eventHandler(handler,fn,idx,cel)
	if fn=="numberOfCellsInTableView" then
		return #self.lbList
	elseif fn=="tableCellSizeForIndex" then
		local tmpSize=self.lbList[idx+1]:getContentSize()
		return  tmpSize
	elseif fn=="tableCellAtIndex" then
		local cell=CCTableViewCell:new()
		cell:autorelease()
		cell:addChild(self.lbList[idx+1])
		return cell
	elseif fn=="ccTouchBegan" then
		self.isMoved=false
		return true
	elseif fn=="ccTouchMoved" then
		self.isMoved=true
	elseif fn=="ccTouchEnded"  then

	end
end